import React, { useEffect } from "react";
import Layout from "../components/Layout";
import MagicGrid from "react-magic-grid";
import styled from "styled-components";
import { Button } from "antd";
import { useSelector, useDispatch } from "react-redux";
import { IMG_LIST_REQUEST } from "../reducers/portfolio";

const SaveButton = styled(Button)`
  position: absolute;
  bottom: 5px;
  right: 5px;

  display: none;
`;

const RednerBox = styled.div`
  width: calc(100% / 8);
  margin: 2px;
  border-radius: 10px;
  height: ${(props) => props.height || "200px"};
  min-height: 200px;

  box-shadow: 0px 0px 5px #ccc;

  transition: 0.4s;
  cursor: pointer;

  position: relative;

  &:hover {
    opacity: 0.5;
  }

  &:hover ${SaveButton} {
    display: flex;
  }
`;

const RenderImg = styled.img`
  width: 100%;
  height: 100%;
  overflow: hidden;

  object-fit: cover;
  border-radius: 10px;
`;

const Home = () => {
  const { imgs } = useSelector((state) => state.portfolio);

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch({
      type: IMG_LIST_REQUEST,
    });
  }, []);

  console.log(imgs);

  const cards = [
    "https://source.unsplash.com/random/400x300",
    "https://source.unsplash.com/random/400x301",
    "https://source.unsplash.com/random/400x302",
    "https://source.unsplash.com/random/400x303",
    "https://source.unsplash.com/random/400x304",
    "https://source.unsplash.com/random/400x305",
    "https://source.unsplash.com/random/400x306",
    "https://source.unsplash.com/random/400x307",
    "https://source.unsplash.com/random/400x308",
    "https://source.unsplash.com/random/400x309",
    "https://source.unsplash.com/random/400x310",
    "https://source.unsplash.com/random/400x311",
    "https://source.unsplash.com/random/400x312",
    "https://source.unsplash.com/random/400x313",
    "https://source.unsplash.com/random/400x314",
    "https://source.unsplash.com/random/400x315",
    "https://source.unsplash.com/random/400x316",
    "https://source.unsplash.com/random/400x317",
    "https://source.unsplash.com/random/400x318",
    "https://source.unsplash.com/random/400x319",
    "https://source.unsplash.com/random/400x320",
    "https://source.unsplash.com/random/400x321",
    "https://source.unsplash.com/random/400x322",
    "https://source.unsplash.com/random/400x323",
    "https://source.unsplash.com/random/400x324",
    "https://source.unsplash.com/random/400x325",
    "https://source.unsplash.com/random/400x326",
    "https://source.unsplash.com/random/400x327",
    "https://source.unsplash.com/random/400x328",
  ];

  return (
    <Layout>
      <MagicGrid items={cards.length}>
        {cards.map((item) => {
          const randomHeight = Math.floor(Math.random() * 400) + "px";

          return (
            <RednerBox height={randomHeight}>
              <RenderImg src={item} alt="image" />
              <SaveButton type="primary">save</SaveButton>
            </RednerBox>
          );
        })}
      </MagicGrid>
    </Layout>
  );
};

export default Home;
