import React from "react";
import styled, { keyframes } from "styled-components";

const HeaderSection = styled.section`
  width: 100%;
  height: 80px;
  padding: 4px 16px;

  display: flex;
  flex-direction: row;
  align-items: center;
`;

const LogoHoverAnimation = keyframes`
0% {
  box-shadow : 0px 0px 20px #999
} 25% {
  box-shadow : 0px 0px 3px #999
} 50% {
  box-shadow : 0px 0px 20px #999
} 75% {
  box-shadow : 0px 0px 3px #999
} 100% {
  box-shadow : 0px 0px 20px #999
}
`;

const LogoImg = styled.img`
  width: 48px;
  heignt: 48px;

  cursor: pointer;

  &:hover {
    animation: ${LogoHoverAnimation} 2s infinite;
  }
`;

const Layout = ({ children }) => {
  return (
    <>
      <HeaderSection>
        <div>
          <LogoImg
            src={`https://firebasestorage.googleapis.com/v0/b/storage-4leaf.appspot.com/o/4leaflender%2Fassets%2Fimages%2Flogo%2Ffavicon.png?alt=media&token=1824f89c-3ee3-4de4-accf-4d0d5c2ee25f`}
            alt="Logo"
          ></LogoImg>
        </div>
        <div>SearchBar</div>
        <div>Icon</div>
        <div>Icon</div>
        <div>Icon</div>
      </HeaderSection>
      <section>{children}</section>
    </>
  );
};

export default Layout;
