import React from "react";
import styled from "styled-components";

const HeaderSection = styled.section`
  width: 100%;
  height: 80px;
  padding: 4px 16px;

  display: flex;
  flex-direction: row;
  align-items: center;
`;

const LogoImg = styled.img`
  width: 48px;
  heignt: 48px;
`;

const Layout = ({ children }) => {
  return (
    <>
      <HeaderSection>
        <div>Icon</div>
        <div>SearchBar</div>
        <div>Icon</div>
        <div>Icon</div>
        <div>Icon</div>
      </HeaderSection>
      <section>{children}</section>
    </>
  );
};

export default Layout;
