import React from "react";
import Layout from "../components/Layout";
import MagicGrid from "react-magic-grid";
import styled from "styled-components";

const RandomBox = styled.div`
  width: calc(100% / 8);
  margin: 2px;
  border-radius: 10px;
  height: ${(props) => props.height || "200px"}
  min-height: 200px;
  box-shadow: 0px 0px 5px #ccc;

  transition: 0.4s;
  cursor: pointer;
  & {
    opacity: 0.5;
  }
`;

const RanderImg = styled.img`
  width: 100%;
  height: 100%;
  overflow: hidden;

  object-fit: cover;
  border-radius: 10px;
`;

const Home = () => {
  const cards = [
    "https://source.unsplash.com/random/400x300",
    "https://source.unsplash.com/random/400x301",
    "https://source.unsplash.com/random/400x302",
    "https://source.unsplash.com/random/400x303",
    "https://source.unsplash.com/random/400x304",
    "https://source.unsplash.com/random/400x305",
    "https://source.unsplash.com/random/400x306",
    "https://source.unsplash.com/random/400x307",
    "https://source.unsplash.com/random/400x308",
    "https://source.unsplash.com/random/400x309",
    "https://source.unsplash.com/random/400x310",
    "https://source.unsplash.com/random/400x311",
    "https://source.unsplash.com/random/400x312",
    "https://source.unsplash.com/random/400x313",
    "https://source.unsplash.com/random/400x314",
    "https://source.unsplash.com/random/400x315",
    "https://source.unsplash.com/random/400x316",
    "https://source.unsplash.com/random/400x317",
  ];

  return (
    <Layout>
      <MagicGrid items={cards.length}>
        {cards.map((item) => {
          const randomHeight = Math.floor(Math.random() * 400) + "px";

          return (
            <RandomBox height={randomHeight}>
              <RanderImg src={item} alt="image" />
            </RandomBox>
          );
        })}
      </MagicGrid>
    </Layout>
  );
};

export default Home;
