import React from "react";
import styled from "styled-components";

const HeaderSection = styled.section`
  width: 100%
  height: 80px
  padding: 4px 16px

  display: flex
  flex-direction:row
  align-items: center
`;

const Layout = ({ children }) => {
  return (
    <>
      <HeaderSection>Heaader</HeaderSection>
      <section>{children}</section>
    </>
  );
};

export default Layout;
